/**
 * Wiki Next Task Selector
 *
 * The main entry point for autonomous agent operation.
 * Uses true randomness combined with ecosystem state to determine:
 * 1. What type of task to perform (repair vs. create)
 * 2. Which specific topic to address
 * 3. Which researcher to feature
 *
 * Returns a complete task specification for the agent.
 */

import { ToolModule } from "../types.js";
import * as fs from "fs";
import * as path from "path";
import * as crypto from "crypto";

const WIKI_DIR = path.join(process.cwd(), "not-wikipedia");
const ECOSYSTEM_FILE = path.join(process.cwd(), "meta", "ecosystem.json");
const RESEARCHERS_FILE = path.join(process.cwd(), "meta", "researchers.json");

interface HumanSeed {
  text: string;
  source: string;
  type: "quote" | "fallback";
}

interface TaskSpec {
  taskType: "repair_broken_link" | "resolve_placeholder" | "fix_orphan" | "create_new" | "ecosystem_healthy";
  priority: "critical" | "high" | "medium" | "low";
  topic: {
    name: string;
    filename: string;
    context: string;
  };
  humanSeed?: HumanSeed;
  researcher: {
    name: string;
    field: string;
    institution: string;
    isNew: boolean;
  } | null;
  articleType: string;
  category: string;
  infoboxColor: string;
  randomSeed: string;
  ecosystemStats: {
    totalArticles: number;
    brokenLinks: number;
    orphans: number;
    placeholders: number;
  };
}

function secureRandomInt(max: number): number {
  if (max <= 0) return 0;
  const randomBytes = crypto.randomBytes(4);
  const randomValue = randomBytes.readUInt32BE(0);
  return randomValue % max;
}

function secureRandomElement<T>(arr: T[]): T | null {
  if (arr.length === 0) return null;
  return arr[secureRandomInt(arr.length)];
}

// Available infobox colors (pick unused ones)
const INFOBOX_COLORS = [
  "#7b9e89", "#c9a86c", "#8b7355", "#d4a87a",
  "#a6c4d4", "#b8a8d4", "#98d1a8", "#e6c88a",
  "#9fc5e8", "#f9cb9c", "#b4a7d6", "#a2c4c9",
];

// Article types for rotation
const ARTICLE_TYPES = [
  "phenomenon", "theory", "methodology", "practice",
  "institution", "biography", "historical_event", "case_study",
];

// Categories to balance
const CATEGORIES = [
  "linguistics", "consciousness", "chronopsychology", "technology", "institutions",
];

// Embedded fallback corpus for human seed inspiration
const FALLBACK_CORPUS: Array<{ text: string; source: string }> = [
  { text: "Call me Ishmael. Some years ago—never mind how long precisely—having little or no money in my purse, and nothing particular to interest me on shore, I thought I would sail about a little and see the watery part of the world.", source: "Herman Melville, Moby-Dick" },
  { text: "It is a truth universally acknowledged, that a single man in possession of a good fortune, must be in want of a wife.", source: "Jane Austen, Pride and Prejudice" },
  { text: "The fog comes on little cat feet. It sits looking over harbor and city on silent haunches and then moves on.", source: "Carl Sandburg, Fog" },
  { text: "I have measured out my life with coffee spoons.", source: "T.S. Eliot, The Love Song of J. Alfred Prufrock" },
  { text: "Whereof one cannot speak, thereof one must be silent.", source: "Ludwig Wittgenstein, Tractatus Logico-Philosophicus" },
  { text: "He who has a why to live can bear almost any how.", source: "Friedrich Nietzsche, Twilight of the Idols" },
  { text: "There is grandeur in this view of life, with its several powers, having been originally breathed into a few forms or into one.", source: "Charles Darwin, On the Origin of Species" },
  { text: "The cosmos is within us. We are made of star-stuff. We are a way for the universe to know itself.", source: "Carl Sagan, Cosmos" },
  { text: "I write entirely to find out what I'm thinking, what I'm looking at, what I see and what it means.", source: "Joan Didion, Why I Write" },
  { text: "Time is the substance I am made of. Time is a river which sweeps me along, but I am the river.", source: "Jorge Luis Borges, A New Refutation of Time" },
  { text: "The past is never dead. It's not even past.", source: "William Faulkner, Requiem for a Nun" },
  { text: "Perhaps one did not want to be loved so much as to be understood.", source: "George Orwell, 1984" },
  { text: "We are what we pretend to be, so we must be careful about what we pretend to be.", source: "Kurt Vonnegut, Mother Night" },
  { text: "The mind is its own place, and in itself can make a heaven of hell, a hell of heaven.", source: "John Milton, Paradise Lost" },
  { text: "Memory is the diary we all carry about with us.", source: "Oscar Wilde" },
  { text: "Two roads diverged in a yellow wood, and sorry I could not travel both.", source: "Robert Frost, The Road Not Taken" },
  { text: "One cannot step twice in the same river.", source: "Heraclitus" },
  { text: "The world is too much with us; late and soon, getting and spending, we lay waste our powers.", source: "William Wordsworth, The World Is Too Much With Us" },
  { text: "So we beat on, boats against the current, borne back ceaselessly into the past.", source: "F. Scott Fitzgerald, The Great Gatsby" },
  { text: "I took a deep breath and listened to the old brag of my heart: I am, I am, I am.", source: "Sylvia Plath, The Bell Jar" },
];

async function fetchHumanSeed(): Promise<HumanSeed> {
  // Try Quotable API first
  try {
    const controller = new AbortController();
    const timeout = setTimeout(() => controller.abort(), 5000);
    const response = await fetch("https://api.quotable.io/random?minLength=50&maxLength=200", {
      signal: controller.signal
    });
    clearTimeout(timeout);
    if (response.ok) {
      const data = await response.json();
      return { text: data.content, source: data.author, type: "quote" };
    }
  } catch {
    // Fall through to fallback
  }

  // Use fallback corpus
  const index = secureRandomInt(FALLBACK_CORPUS.length);
  const passage = FALLBACK_CORPUS[index];
  return { text: passage.text, source: passage.source, type: "fallback" };
}

function getEcosystemState() {
  const state = {
    brokenLinks: [] as { target: string; sources: string[] }[],
    placeholders: [] as string[],
    orphans: [] as string[],
    articleCount: 0,
    usedColors: [] as string[],
    categoryBalance: {} as Record<string, number>,
  };

  try {
    const files = fs.readdirSync(WIKI_DIR).filter(f => f.endsWith(".html"));
    state.articleCount = files.length;

    const incomingLinks: Map<string, number> = new Map();
    files.forEach(f => incomingLinks.set(f, 0));

    for (const file of files) {
      const filePath = path.join(WIKI_DIR, file);
      const content = fs.readFileSync(filePath, "utf-8");

      // Check broken links
      const linkMatches = content.matchAll(/href="([^"]*\.html)"/g);
      for (const match of linkMatches) {
        const link = match[1];
        if (!fs.existsSync(path.join(WIKI_DIR, link))) {
          const existing = state.brokenLinks.find(b => b.target === link);
          if (existing) {
            existing.sources.push(file);
          } else {
            state.brokenLinks.push({ target: link, sources: [file] });
          }
        } else {
          incomingLinks.set(link, (incomingLinks.get(link) || 0) + 1);
        }
      }

      // Check placeholders
      if (content.includes("NEXT_PAGE_PLACEHOLDER")) {
        state.placeholders.push(file);
      }

      // Extract used colors
      const colorMatch = content.match(/background-color:\s*(#[a-fA-F0-9]{6})/);
      if (colorMatch) {
        state.usedColors.push(colorMatch[1].toLowerCase());
      }
    }

    // Find orphans
    for (const [file, count] of incomingLinks) {
      if (file !== "index.html" && count === 0) {
        state.orphans.push(file);
      }
    }

    // Load category balance
    if (fs.existsSync(ECOSYSTEM_FILE)) {
      const ecosystem = JSON.parse(fs.readFileSync(ECOSYSTEM_FILE, "utf-8"));
      state.categoryBalance = {};
      for (const [cat, data] of Object.entries(ecosystem.categories || {})) {
        state.categoryBalance[cat] = (data as any).article_count || 0;
      }
    }

  } catch (error) {
    // Continue with partial state
  }

  return state;
}

function getAvailableResearcher(): { name: string; field: string; institution: string; isNew: boolean } | null {
  try {
    if (fs.existsSync(RESEARCHERS_FILE)) {
      const data = JSON.parse(fs.readFileSync(RESEARCHERS_FILE, "utf-8"));

      // 20% chance to suggest new researcher
      if (secureRandomInt(5) === 0 && data.suggested_new_researchers?.length > 0) {
        const newR = secureRandomElement(data.suggested_new_researchers) as { name: string; field: string; institution: string } | null;
        if (newR) {
          return {
            name: newR.name,
            field: newR.field,
            institution: newR.institution,
            isNew: true,
          };
        }
      }

      // Get available researchers
      const available: any[] = [];
      for (const [key, r] of Object.entries(data.researchers || {})) {
        const researcher = r as any;
        if (researcher.status?.includes("AVAILABLE")) {
          available.push(researcher);
        }
      }

      if (available.length > 0) {
        const selected = secureRandomElement(available);
        if (selected) {
          return {
            name: selected.name,
            field: selected.field,
            institution: selected.institution,
            isNew: false,
          };
        }
      }
    }
  } catch (error) {
    // Return null on error
  }
  return null;
}

async function selectNextTask(): Promise<TaskSpec> {
  const randomSeed = crypto.randomBytes(8).toString("hex");
  const state = getEcosystemState();
  const researcher = getAvailableResearcher();

  // Pick unused color
  const availableColors = INFOBOX_COLORS.filter(c => !state.usedColors.includes(c.toLowerCase()));
  const infoboxColor = secureRandomElement(availableColors) || secureRandomElement(INFOBOX_COLORS) || "#b0c4de";

  // Pick article type (rotate through them)
  const articleType = secureRandomElement(ARTICLE_TYPES) || "phenomenon";

  // Pick underrepresented category
  let category = "linguistics";
  const minCount = Math.min(...Object.values(state.categoryBalance).filter(v => v > 0), 999);
  const underrepresented = Object.entries(state.categoryBalance)
    .filter(([_, count]) => count <= minCount + 1)
    .map(([cat, _]) => cat);
  if (underrepresented.length > 0) {
    category = secureRandomElement(underrepresented) || category;
  }

  const ecosystemStats = {
    totalArticles: state.articleCount,
    brokenLinks: state.brokenLinks.length,
    orphans: state.orphans.length,
    placeholders: state.placeholders.length,
  };

  // Priority 1: Broken links (CRITICAL)
  if (state.brokenLinks.length > 0) {
    // Sort by number of sources (most referenced first)
    state.brokenLinks.sort((a, b) => b.sources.length - a.sources.length);
    const selected = secureRandomElement(state.brokenLinks) || state.brokenLinks[0];
    const topicName = selected.target.replace(".html", "").split("-")
      .map(w => w.charAt(0).toUpperCase() + w.slice(1)).join(" ");

    return {
      taskType: "repair_broken_link",
      priority: "critical",
      topic: {
        name: topicName,
        filename: selected.target,
        context: `Referenced by ${selected.sources.length} article(s): ${selected.sources.join(", ")}`,
      },
      researcher,
      articleType,
      category,
      infoboxColor,
      randomSeed,
      ecosystemStats,
    };
  }

  // Priority 2: Placeholders (HIGH)
  if (state.placeholders.length > 0) {
    const sourceFile = secureRandomElement(state.placeholders) || state.placeholders[0];
    return {
      taskType: "resolve_placeholder",
      priority: "high",
      topic: {
        name: "PLACEHOLDER_RESOLUTION",
        filename: sourceFile,
        context: `File ${sourceFile} contains unresolved NEXT_PAGE_PLACEHOLDER`,
      },
      researcher,
      articleType,
      category,
      infoboxColor,
      randomSeed,
      ecosystemStats,
    };
  }

  // Priority 3: Orphans (MEDIUM)
  if (state.orphans.length > 0) {
    const orphan = secureRandomElement(state.orphans) || state.orphans[0];
    return {
      taskType: "fix_orphan",
      priority: "medium",
      topic: {
        name: orphan.replace(".html", ""),
        filename: orphan,
        context: `This article has no incoming links. Add references from related articles.`,
      },
      researcher,
      articleType,
      category,
      infoboxColor,
      randomSeed,
      ecosystemStats,
    };
  }

  // Priority 4: Create new content (LOW - ecosystem is healthy)
  // Fetch human-written text as creative inspiration
  const humanSeed = await fetchHumanSeed();

  return {
    taskType: "create_new",
    priority: "low",
    topic: {
      name: "INSPIRED_BY_SEED",
      filename: "to-be-determined.html",
      context: "Ecosystem is healthy. Use the humanSeed as a topic spark to create new content.",
    },
    humanSeed,
    researcher,
    articleType,
    category,
    infoboxColor,
    randomSeed,
    ecosystemStats,
  };
}

export const tool: ToolModule = {
  definition: {
    name: "wiki_next_task",
    description: "Get the next task for the Not-Wikipedia autonomous agent. Uses cryptographically secure randomness combined with ecosystem state to select: task type, researcher, article type, category, and infobox color. For new content creation, fetches a human-written text passage (quote or literary excerpt) as creative inspiration. Returns a complete task specification.",
    inputSchema: {
      type: "object",
      properties: {},
      required: [],
    },
  },

  handler: async () => {
    const task = await selectNextTask();
    return {
      content: [{
        type: "text",
        text: JSON.stringify(task, null, 2),
      }],
    };
  },
};

