from html.parser import HTMLParser
import sys

class Validator(HTMLParser):
    def __init__(self):
        super().__init__()
        self.errors = []
    def error(self, message):
        self.errors.append(message)

with open('primordial-lexeme-hypothesis.html') as f:
    content = f.read()

v = Validator()
try:
    v.feed(content)
    if v.errors:
        print('Errors:', v.errors)
    else:
        print('HTML parses successfully')
except Exception as e:
    print(f'Parse error: {e}')
